  const proxyUrl = 'http://localhost:3001/api/shipment/fee';

  async function getShippingFeeValue() {
    const url = 'https://services.giaohangtietkiem.vn/services/shipment/fee';
    const token = '999232dcc709d6426b3d677c2cb19c64057f3f52';
  
    const requestBody = {
      pick_province: 'HCM city',
      pick_district: 'Quận 5',
      province: 'Hà nội',
      district: 'Quận Cầu Giấy',
      address: '',
      weight: '',
      value: 0,
      transport: 'road',
      deliver_option: 'none',
      tags: [1]
    };
  
    const requestOptions = {
     // mode: 'no-cors',
      method: 'POST',
      headers: {
       // 'Token': token,
        'Host': 'services.giaohangtietkiem.vn',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
        
      },
      body: JSON.stringify(requestBody)
    };
  
    try {
      const response = await fetch(proxyUrl, requestOptions);
      const data = await response.json();
      
      if (data.success && data.fee && data.fee.fee) {
        console.log("data", data.fee.fee )
        return data.fee.fee;
      } else {
        throw new Error('Invalid response format or missing data.');
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      return null;
    }
  }
  getShippingFeeValue();

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { cart, userInfo } = state;

  const round2 = (num) => Math.round(num * 100 + Number.EPSILON) / 100; // 123.2345 => 123.23
  cart.itemsPrice = round2(
    cart.cartItems.reduce((a, c) => a + c.quantity * c.price, 0)
  );
  
  cart.shippingPrice = 0;
  cart.taxPrice = round2(0.15 * cart.itemsPrice);
  cart.totalPrice = cart.itemsPrice + cart.shippingPrice + cart.taxPrice;

  const placeOrderHandler = async () => {

    try {
      dispatch({ type: 'CREATE_REQUEST' });

      const { data } = await Axios.post(
        '/api/orders',
        {
          orderItems: cart.cartItems,
          shippingAddress: cart.shippingAddress,
          paymentMethod: cart.paymentMethod,
          itemsPrice: cart.itemsPrice,
          shippingPrice: cart.shippingPrice,
          taxPrice: cart.taxPrice,
          totalPrice: cart.totalPrice,
        },
        {
          headers: {
            authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      ctxDispatch({ type: 'CART_CLEAR' });
      dispatch({ type: 'CREATE_SUCCESS' });
      localStorage.removeItem('cartItems');
      navigate(`/order/${data.order._id}`);
    } catch (err) {
      dispatch({ type: 'CREATE_FAIL' });
      toast.error(getError(err));
    }
  };
